import { useEffect, useState } from 'react';
import classes from './Search.module.css';
import SpotifyWebApi from 'spotify-web-api-js';
import Category from './Category';

const spotify = new SpotifyWebApi();

export default function Search(props) {
  //   const [categoriesImages, setCategoriesImages] = useState([]);
  var categoriesList = [];
  useEffect(() => {
    props.categories.forEach((cat) => {
      spotify
        .getCategoryPlaylists(cat.id)
        .then((res) => {
          cat = { ...cat, image: res.playlists.items[0].images[0].url };
        })
        .catch((error) => console.log(error.message));
    });

    categoriesList = props.categories.map((cat) => {
      return (
        <Category key={cat.id} id={cat.id} name={cat.name} image={cat.image} />
      );
    });
  }, []);

  return (
    <div className={classes.searchPage}>
      <h1 className={classes.headline}>Browse all</h1>
      <ul className={classes.searchCategories}>{categoriesList}</ul>
    </div>
  );
}
