import classes from './Main.module.css';
import HomeIcon from '@mui/icons-material/Home';
import HomeOutlinedIcon from '@mui/icons-material/HomeOutlined';
import { useState } from 'react';
const Main = (props) => {
  const [isHomePressed, setIsHomePressed] = useState(false);

  return (
    <div className={classes.main}>
      <div className={classes.img}>
        <img
          className={classes.logo}
          src="https://storage.googleapis.com/pr-newsroom-wp/1/2018/11/Spotify_Logo_RGB_White.png"
          alt="logo"
        ></img>
      </div>
      <div className={classes.mainNavigation}>
        <nav>
          <ul>
            <div>
              {isHomePressed ? <HomeOutlinedIcon /> : <HomeIcon />}
              <li className={classes.navAction}>Home</li>
            </div>
            <li className={classes.navAction}>Search</li>
            <li className={classes.navAction}>Your Library</li>
          </ul>
        </nav>
      </div>
      <div className={classes.more}></div>
    </div>
  );
};

export default Main;
