import classes from './Footer.module.css';
import SpotifyWebApi from 'spotify-web-api-js';
import { useEffect, useState } from 'react';

const spotify = new SpotifyWebApi();

export default function Footer(props) {
  const helperArtists = [];
  props.currentTrack.item?.artists.forEach((artist) => {
    helperArtists.push(artist.name);
  });

  const artists = helperArtists.join(', ');
  console.log(props.currentTrack.item);

  return (
    <div className={classes.footer}>
      <div className={classes.currentTrack}>
        <img
          className={classes.currentImg}
          src={props.currentTrack.item?.album.images[1].url}
          alt={props.currentTrack.item?.album.name}
        />
        <div className={classes.currentTrackDetails}>
          <h1 className={classes.title}>{props.currentTrack.item?.name}</h1>
          <h5 className={classes.artists}>{artists}</h5>
        </div>
      </div>
    </div>
  );
}
