import React, { useEffect, useState } from 'react';
import classes from './TimingSlider.module.css';
import { Slider } from '@mui/material';
import SpotifyWebApi from 'spotify-web-api-js';

const spotify = new SpotifyWebApi();

export default function TimingSlider(props) {
  const [position, setPosition] = useState(0);
  const [currentTrack, setCurrentTrack] = useState({});

  function millisToMinutesAndSeconds(millis) {
    var minutes = Math.floor(millis / 60000);
    var seconds = Math.floor((millis % 60000) / 1000).toFixed(0);
    return minutes + ':' + (seconds < 10 ? '0' : '') + seconds;
  }

  let initialTiming = millisToMinutesAndSeconds(position);
  let finalTiming = millisToMinutesAndSeconds(currentTrack.item?.duration_ms);

  useEffect(() => {
    setTimeout(() => {
      spotify
        .getMyCurrentPlayingTrack()
        .then((res) => setCurrentTrack(res))
        .catch((error) => console.log(error.message));
    }, 1100);

    console.log(currentTrack);

    setPosition(currentTrack.progress_ms);
  }, [currentTrack.progress_ms]);

  return (
    <div className={classes.timingSlider}>
      <div className={classes.timing}>
        {isNaN(position) ? '0:00' : initialTiming}
      </div>
      <Slider
        key={0}
        className={classes.slider}
        aria-label="Volume"
        value={position}
        onChange={(event) => setPosition(event.target.value)}
        step={1}
        max={currentTrack.item?.duration_ms}
        size="small"
        color="success"
        sx={{
          color: 'white',
          height: 4,
          '& .MuiSlider-thumb': {
            color: 'transparent',
            boxShadow: 'none',
          },
          '& .MuiSlider-thumb:hover': {
            color: 'white',
          },
          '& .MuiSlider-thumb:active': {
            color: 'white',
          },
          '& .MuiSlider-rail': {
            color: 'white',
          },
          '&:hover, &.Mui-focusVisible, &.Mui-active': {
            boxShadow: 'none',
          },
        }}
      />
      <div className={classes.timing}>{finalTiming}</div>
    </div>
  );
}
