import { useEffect, useState } from 'react';
import classes from './Search.module.css';
import SpotifyWebApi from 'spotify-web-api-js';
import Category from './Category';

const spotify = new SpotifyWebApi();

export default function Search() {
  const [categories, setCategories] = useState([]);
  const [categoriesImages, setCategoriesImages] = useState([]);
  const cat = {};

  useEffect(() => {
    spotify
      .getCategories()
      .then((res) => {
        cat.push(res.categories.items);
        console.log(cat);
      })
      .catch((error) => console.log(error.message));

    cat.forEach((catItem) => {
      spotify
        .getCategoryPlaylists(catItem.id)
        .then((res) => {
          console.log(catItem);
          setCategoriesImages(res.playlists.items[0].images[0].url);
        })
        .catch((error) => console.log(error.message));
    });
  }, []);

  const newCategories = cat.map((element) => ({
    ...element,
    image: categoriesImages,
  }));

  const categoriesList = newCategories.map((cat) => {
    return (
      <Category key={cat.id} id={cat.id} name={cat.name} image={cat.image} />
    );
  });

  return (
    <div className={classes.searchPage}>
      <h1 className={classes.headline}>Browse all</h1>
      <ul className={classes.searchCategories}>{categoriesList}</ul>
    </div>
  );
}
