import './App.css';
import Header from './Header/Header';
import Main from './sidebar/Main';
import DefaultPage from './pages/DefaultPage';
import { useContext, useEffect, useState } from 'react';
import Login from './Login';
import { getTokenFromUri } from './Login';
import SpotifyWebApi from 'spotify-web-api-js';
import DataLayerContext from './store/DataLayerContext';

const spotify = new SpotifyWebApi();

const App = () => {
  const ctx = useContext(DataLayerContext);
  const [isSearchPressed, setIsSearchPressed] = useState(false);
  const [token, setToken] = useState(null);

  useEffect(() => {
    const hash = getTokenFromUri();
    window.location.hash = '';
    const received_token = hash.access_token;
    if (received_token) {
      setToken(received_token);
      spotify.setAccessToken(received_token);
      spotify.getMe().then((user) => ctx.loginUser(user));
    }
  }, []);

  console.log(ctx.user);

  return (
    <div className="App">
      {!token && <Login />}
      {token && (
        <Main onSearch={setIsSearchPressed} searchTerm={isSearchPressed} />
      )}
      {token && (
        <div className="mainPages">
          <Header searchTerm={isSearchPressed} />
          <DefaultPage />
        </div>
      )}
    </div>
  );
};

export default App;
