import React, { useState } from 'react';
import classes from './TimingSlider.module.css';
import { Slider } from '@mui/material';

export default function TimingSlider(props) {
  const [position, setPosition] = React.useState(32);

  function formatDuration(value: number) {
    const minute = Math.floor(value / 60);
    const secondLeft = value - minute * 60;
    return `${minute}:${secondLeft < 10 ? `0${secondLeft}` : secondLeft}`;
  }

  let initialTiming = formatDuration(position);
  let finalTiming = formatDuration(props.currEndTiming);

  return (
    <div className={classes.timingSlider}>
      <div className={classes.timing}>{initialTiming}</div>
      <Slider
        className={classes.slider}
        aria-label="Volume"
        value={position}
        onChange={(event) => setPosition(event.target.value)}
        step={1}
        max={finalTiming}
        size="small"
        color="success"
        sx={{
          color: 'white',
          height: 4,
          '& .MuiSlider-thumb': {
            color: 'transparent',
          },
          '& .MuiSlider-thumb:hover': {
            color: 'white',
          },
          '& .MuiSlider-thumb:active': {
            color: 'white',
          },
          '& .MuiSlider-rail': {
            color: 'white',
          },
        }}
      />
      <div className={classes.timing}>{finalTiming}</div>
    </div>
  );
}
