import React, { useState } from 'react';
import classes from './TimingSlider.module.css';
import { Slider } from '@mui/material';

export default function TimingSlider(props) {
  const [currentTiming, setCurrentTiming] = useState(0);
  let timing;

  const handleChange = (event) => {
    setCurrentTiming(event.target.value);
  };

  console.log(currentTiming);

  function millisToMinutesAndSeconds(millis) {
    var minutes = Math.floor(millis / 60000);
    var seconds = Math.floor((millis % 60000) / 1000).toFixed(0);
    return minutes + ':' + (seconds < 10 ? '0' : '') + seconds;
  }

  let initialTiming = millisToMinutesAndSeconds(timing) / currentTiming;
  let finalTiming = millisToMinutesAndSeconds(props.currEndTiming);

  return (
    <div className={classes.timingSlider}>
      <div className={classes.timing}>{initialTiming ? initialTiming : 0}</div>
      <Slider
        className={classes.slider}
        aria-label="Volume"
        value={timing}
        onChange={handleChange}
        size="small"
        color="success"
        sx={{
          color: 'white',
          height: 4,
          '& .MuiSlider-thumb': {
            color: 'transparent',
          },
          '& .MuiSlider-thumb:hover': {
            color: 'white',
          },
          '& .MuiSlider-thumb:active': {
            color: 'white',
          },
          '& .MuiSlider-rail': {
            color: 'white',
          },
        }}
      />
      <div className={classes.timing}>{finalTiming}</div>
    </div>
  );
}
