import React from 'react';
import classes from './TimingSlider.module.css';
import { Slider } from '@mui/material';

export default function TimingSlider(props) {
  let timing;

  const handleChange = (event) => {
    timing = event.target.value;
  };

  let initialTiming = '0:00';
  let finalTiming = props?.currEndTiming;

  return (
    <div className={classes.timingSlider}>
      <div className={classes.timing}>{initialTiming}</div>
      <Slider
        className={classes.slider}
        aria-label="Volume"
        value={timing}
        onChange={handleChange}
        size="small"
        color="success"
        sx={{
          color: 'white',
          height: 4,
          '& .MuiSlider-thumb': {
            color: 'transparent',
          },
          '& .MuiSlider-thumb:hover': {
            color: 'white',
          },
          '& .MuiSlider-thumb:active': {
            color: 'white',
          },
          '& .MuiSlider-rail': {
            color: 'white',
          },
        }}
      />
      <div className={classes.timing}>{finalTiming}</div>
    </div>
  );
}
