import classes from './Footer.module.css';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import PictureInPictureAltIcon from '@mui/icons-material/PictureInPictureAlt';
import ShuffleIcon from '@mui/icons-material/Shuffle';
import SkipPreviousIcon from '@mui/icons-material/SkipPrevious';
import SkipNextIcon from '@mui/icons-material/SkipNext';
import RepeatIcon from '@mui/icons-material/Repeat';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import Volume from './Volume';
import TimingSlider from './TimingSlider';

export default function Footer(props) {
  const helperArtists = [];
  props.currentTrack.item?.artists.forEach((artist) => {
    helperArtists.push(artist.name);
  });

  const artists = helperArtists.join(', ');
  console.log(props.currentTrack.item);

  return (
    <div className={classes.footer}>
      <div className={classes.currentTrack}>
        <img
          className={classes.currentImg}
          src={props.currentTrack.item?.album.images[1].url}
          alt={props.currentTrack.item?.album.name}
        />
        <div className={classes.currentTrackDetails}>
          <h1 className={classes.title}>{props.currentTrack.item?.name}</h1>
          <h5 className={classes.artists}>{artists}</h5>
        </div>
        <div className={classes.currentTrackIcons}>
          <FavoriteBorderIcon className={classes.playerIcon} />
          <PictureInPictureAltIcon className={classes.playerIcon} />
        </div>
      </div>
      <div className={classes.footerMainButtons}>
        <div className={classes.footerMainButtonsIcons}>
          <ShuffleIcon className={classes.footerMainButtonsIcon} />
          <SkipPreviousIcon className={classes.footerMainButtonsBackForward} />
          <PlayArrowIcon className={classes.PlayButton} />
          <SkipNextIcon className={classes.footerMainButtonsBackForward} />
          <RepeatIcon className={classes.footerMainButtonsIcon} />
        </div>
        <div>
          <div className={classes.timing} defaultValue="0"></div>
          <TimingSlider className={classes.footerMainButtonsSlider} />
          <div className={classes.timing}></div>
        </div>
      </div>
      <Volume />
    </div>
  );
}
