import { useEffect, useState } from 'react';
import classes from './Search.module.css';
import SpotifyWebApi from 'spotify-web-api-js';
import Category from './Category';

const spotify = new SpotifyWebApi();

export default function Search() {
  const [categories, setCategories] = useState([]);
  const categoriesImages = [];
  useEffect(() => {
    spotify
      .getCategories()
      .then((res) => {
        setCategories(res.categories.items);
      })
      .catch((error) => console.log(error.message));

    setTimeout(() => {
      categories.forEach((cat) => {
        spotify
          .getCategoryPlaylists(cat.id)
          .then((res) => {
            console.log(res);
            categoriesImages.push(res.playlists.items[0].images[0].url);
          })
          .catch((error) => console.log(error.message));
      });
    }, 100);
  }, []);

  const newCategories = categories.map((element) => ({
    ...element,
    image: categoriesImages,
  }));

  const categoriesList = newCategories.map((cat) => {
    return (
      <Category key={cat.id} id={cat.id} name={cat.name} image={cat.image} />
    );
  });

  return (
    <div className={classes.searchPage}>
      <h1 className={classes.headline}>Browse all</h1>
      <ul className={classes.searchCategories}>{categoriesList}</ul>
    </div>
  );
}
