import classes from './Footer.module.css';
import SpotifyWebApi from 'spotify-web-api-js';
import { useEffect, useState } from 'react';

const spotify = new SpotifyWebApi();

export default function Footer(props) {
  console.log(props.currentTrack);
  const helperArtists = [];
  props.currentTrack.item.artists.forEach((artist) => {
    helperArtists.push(artist.name);
  });

  const artists = helperArtists.map((item) => (
    <div style={{ color: 'white' }}>{item}</div>
  ));

  return (
    <div className={classes.footer}>
      <div>
        <h1>{props.currentTrack.item?.name}</h1>
        <h5>{artists}</h5>
      </div>
    </div>
  );
}
